pragma solidity ^0.8.0;

import { IEntropyConsumer } from "@pythnetwork/entropy-sdk-solidity/IEntropyConsumer.sol";
import { IEntropy } from "@pythnetwork/entropy-sdk-solidity/IEntropy.sol";

contract Climb is IEntropyConsumer {
  IEntropy entropy;

  // Public variable to store the latest random number
  bytes32 public latestRandomNumber;

  // Mapping to store climb results for each sequence number
  mapping(uint64 => bool) public climbResults;

  // Mapping to store the requester for each sequence number
  mapping(uint64 => address) public climbRequesters;

  // Event to emit when a climb is completed
  event ClimbCompleted(uint64 indexed sequenceNumber, address indexed player, bytes32 randomNumber, bool success);

  // Event to emit when a climb is requested
  event ClimbRequested(uint64 indexed sequenceNumber, address indexed player, bytes32 userRandomNumber);

  // @param entropyAddress The address of the entropy contract.
  constructor(address entropyAddress) {
    entropy = IEntropy(entropyAddress);
  }

  // @param userRandomNumber The random number generated by the user.
  function requestRandomNumber(bytes32 userRandomNumber) external payable {
    // Get the default provider and the fee for the request
    address entropyProvider = entropy.getDefaultProvider();
    uint256 fee = entropy.getFee(entropyProvider);

    // Request the random number with the callback
    uint64 sequenceNumber = entropy.requestWithCallback{ value: fee }(
      entropyProvider,
      userRandomNumber
    );
    // Store the sequence number to identify the callback request
  }

  // Main climb function that initiates a random number request
  function climb() external payable returns (uint64) {
    // Generate a user random number based on block data and sender
    bytes32 userRandomNumber = keccak256(abi.encodePacked(
      block.timestamp,
      block.difficulty,
      msg.sender,
      blockhash(block.number - 1)
    ));

    // Get the default provider and the fee for the request
    address entropyProvider = entropy.getDefaultProvider();
    uint256 fee = entropy.getFee(entropyProvider);

    // Ensure sufficient payment
    require(msg.value >= fee, "Insufficient fee for entropy request");

    // Request the random number with the callback
    uint64 sequenceNumber = entropy.requestWithCallback{ value: fee }(
      entropyProvider,
      userRandomNumber
    );

    // Store the requester for this sequence number
    climbRequesters[sequenceNumber] = msg.sender;

    // Emit event for the climb request
    emit ClimbRequested(sequenceNumber, msg.sender, userRandomNumber);

    // Return the sequence number so the caller can track their request
    return sequenceNumber;
  }

  // @param sequenceNumber The sequence number of the request.
  // @param provider The address of the provider that generated the random number. If your app uses multiple providers, you can use this argument to distinguish which one is calling the app back.
  // @param randomNumber The generated random number.
  // This method is called by the entropy contract when a random number is generated.
  // This method **must** be implemented on the same contract that requested the random number.
  // This method should **never** return an error -- if it returns an error, then the keeper will not be able to invoke the callback.
  // If you are having problems receiving the callback, the most likely cause is that the callback is erroring.
  // See the callback debugging guide here to identify the error https://docs.pyth.network/entropy/debug-callback-failures
  function entropyCallback(
    uint64 sequenceNumber,
    address provider,
    bytes32 randomNumber
  ) internal override {
    // Store the latest random number
    latestRandomNumber = randomNumber;

    // Convert bytes32 to uint256 for modulo operation
    uint256 randomUint = uint256(randomNumber);

    // Determine if climb succeeds (true if even, false if odd)
    bool climbSuccess = (randomUint % 2 == 0);

    // Store the result
    climbResults[sequenceNumber] = climbSuccess;

    // Get the requester address
    address requester = climbRequesters[sequenceNumber];

    // Emit the completion event
    emit ClimbCompleted(sequenceNumber, requester, randomNumber, climbSuccess);
  }

  // Function to check the result of a specific climb request
  function getClimbResult(uint64 sequenceNumber) external view returns (bool) {
    return climbResults[sequenceNumber];
  }

  // Function to check who made a specific climb request
  function getClimbRequester(uint64 sequenceNumber) external view returns (address) {
    return climbRequesters[sequenceNumber];
  }

  // This method is required by the IEntropyConsumer interface.
  // It returns the address of the entropy contract which will call the callback.
  function getEntropy() internal view override returns (address) {
    return address(entropy);
  }
}
