contracts/GameMaster.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IGame.sol";
import "./Points.sol";

// Import GameState enum from IGame
import {GameState} from "./interfaces/IGame.sol";

/**
 * @title GameMaster
 * @notice Combined contract that handles player registration and game management
 */
contract GameMaster is Ownable {
    // =============================================================
    // ==================== Constants ==============================
    // =============================================================
    uint256 public maxPlayers = 1000;
    Points public pointsContract;

    // =============================================================
    // ================== Player Registration ======================
    // =============================================================
    address[] public registeredPlayers;
    mapping(address => bool) public isRegistered;
    bool public registrationClosed;
    uint256 public registrationFee = 12 ether;

    // =============================================================
    // ================== Active Player Tracking ==================
    // =============================================================
    address[] public activePlayers;
    mapping(address => bool) public isActivePlayer;
    mapping(address => uint256) public playerNumbers;

    // Track eliminated players in order of elimination
    address[] public eliminatedPlayers;
    // Track final placement (1000 = first eliminated, 1 = winner)
    mapping(address => uint256) public finalPlacements;

    // =============================================================
    // ================== Game Management =========================
    // =============================================================
    mapping(string => address) public gameAddresses;
    mapping(string => bool) public isGameRegistered;
    string[] public registeredGames;

    // =============================================================
    // ======================= Events =============================
    // =============================================================

    // Registration Events
    event PlayerRegistered(address indexed player, uint256 playerNumber);
    event RegistrationClosed();
    event RegistrationFeeChanged(uint256 newFee);
    event PlayerRefunded(address indexed player, uint256 amount);
    event RefundFailed(address indexed player, uint256 amount);
    event GameReset();

    // Game Management Events
    event PlayersRegistered(address[] players);
    event GameRegistered(string gameName, address gameAddress);
    event GameStarted(string gameName, address gameAddress, uint256 gameId);
    event PlayerNumberAssigned(address indexed player, uint256 number);
    event PlayerEliminated(address indexed player, uint256 playerNumber);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Sets the Points contract address
     * @param _pointsContract Address of the Points contract
     */
    function setPointsContract(address payable _pointsContract) external onlyOwner {
        require(_pointsContract != address(0), "Invalid Points contract address");
        pointsContract = Points(_pointsContract);
    }

    // =============================================================
    // ================== Player Registration Functions ===========
    // =============================================================

    /**
     * @notice Sets the registration fee
     * @param _newFee New fee amount in wei
     */
    function setRegistrationFee(uint256 _newFee) external onlyOwner {
        require(_newFee > 0, "Fee must be greater than 0");
        registrationFee = _newFee;
        emit RegistrationFeeChanged(_newFee);
    }

    /**
     * @notice Register for the game by sending ETH
     */
    function register() external payable {
        require(!registrationClosed, "Registration is closed");
        require(msg.value >= registrationFee, "Must send at least the registration fee");
        require(!isRegistered[msg.sender], "Already registered");
        require(address(pointsContract) != address(0), "Points contract not set");

        // Calculate points portion (1/6 of registration fee)
        uint256 pointsPortion = registrationFee / 6;

        // Send points portion to Points contract
        pointsContract.depositFor{value: pointsPortion}(msg.sender);

        // Assign player number
        uint256 playerNumber = registeredPlayers.length;
        playerNumbers[msg.sender] = playerNumber;

        registeredPlayers.push(msg.sender);
        isRegistered[msg.sender] = true;

        // Add to active players as well
        activePlayers.push(msg.sender);
        isActivePlayer[msg.sender] = true;

        emit PlayerRegistered(msg.sender, playerNumber);

        // Auto-close registration when max players reached
        if (registeredPlayers.length >= maxPlayers) {
            registrationClosed = true;
            emit RegistrationClosed();
        }

        // Return excess payment if any
        uint256 excess = msg.value - registrationFee;
        if (excess > 0) {
            (bool success, ) = msg.sender.call{value: excess}("");
            require(success, "Failed to return excess payment");
        }
    }

    /**
     * @notice Register for the game with a referral code
     * @param referralCode The referral code to use
     */
    function registerWithReferral(string calldata referralCode) external payable {
        require(!registrationClosed, "Registration is closed");
        require(msg.value >= registrationFee, "Must send at least the registration fee");
        require(!isRegistered[msg.sender], "Already registered");
        require(address(pointsContract) != address(0), "Points contract not set");

        // Calculate points portion (1/6 of registration fee)
        uint256 pointsPortion = registrationFee / 6;

        // Send points portion to Points contract with referral
        pointsContract.depositFor{value: pointsPortion}(msg.sender, referralCode);

        // Assign player number
        uint256 playerNumber = registeredPlayers.length;
        playerNumbers[msg.sender] = playerNumber;

        registeredPlayers.push(msg.sender);
        isRegistered[msg.sender] = true;

        // Add to active players as well
        activePlayers.push(msg.sender);
        isActivePlayer[msg.sender] = true;

        emit PlayerRegistered(msg.sender, playerNumber);

        // Auto-close registration when max players reached
        if (registeredPlayers.length >= maxPlayers) {
            registrationClosed = true;
            emit RegistrationClosed();
        }

        // Return excess payment if any
        uint256 excess = msg.value - registrationFee;
        if (excess > 0) {
            (bool success, ) = msg.sender.call{value: excess}("");
            require(success, "Failed to return excess payment");
        }
    }

    /**
     * @notice Close registration (owner only)
     */
    function closeRegistration() external onlyOwner {
        registrationClosed = true;
        emit RegistrationClosed();
    }

    /**
     * @notice Allows the contract owner to withdraw all collected registration fees
     * @dev Uses low-level call to transfer ETH to avoid gas limitations of transfer()
     * @return true if the withdrawal was successful
     */
    function withdraw() external onlyOwner returns (bool) {
        uint256 contractBalance = address(this).balance;
        require(contractBalance > 0, "No funds to withdraw");

        address ownerAddress = owner();
        (bool transferSuccess, ) = ownerAddress.call{value: contractBalance}("");
        require(transferSuccess, "ETH transfer to owner failed");

        return true;
    }

    /**
     * @notice Reset game and refund all registered players
     */
    function resetGame() external onlyOwner {
        require(!registrationClosed || registeredPlayers.length > 0, "No players to refund");

        // Refund each registered player
        for (uint256 i = 0; i < registeredPlayers.length; i++) {
            address player = registeredPlayers[i];
            (bool success, ) = player.call{value: registrationFee}("");

            // Log failure but continue to avoid locking funds
            if (!success) {
                emit RefundFailed(player, registrationFee);
            } else {
                emit PlayerRefunded(player, registrationFee);
            }

            // Clear final placement
            delete finalPlacements[player];
        }

        // Reset state variables
        delete registeredPlayers;
        delete activePlayers;
        delete eliminatedPlayers;
        registrationClosed = false;

        // Clear player tracking
        for (uint i = 0; i < registeredPlayers.length; i++) {
            address player = registeredPlayers[i];
            isRegistered[player] = false;
            isActivePlayer[player] = false;
            delete playerNumbers[player];
        }

        emit GameReset();
    }

    /**
     * @notice Allows owner to register multiple players without requiring registration fee
     * @dev Only callable by contract owner, useful for special events or testing
     * @param players Array of player addresses to register
     */
    function registerPlayersBatch(address[] calldata players) external onlyOwner {
        require(!registrationClosed, "Registration is closed");
        require(registeredPlayers.length + players.length <= maxPlayers, "Would exceed maximum players");

        for (uint256 i = 0; i < players.length; i++) {
            address player = players[i];
            require(player != address(0), "Invalid player address");
            require(!isRegistered[player], "Player already registered");

            // Assign player number
            uint256 playerNumber = registeredPlayers.length;
            playerNumbers[player] = playerNumber;

            // Register player
            registeredPlayers.push(player);
            isRegistered[player] = true;

            // Add to active players
            activePlayers.push(player);
            isActivePlayer[player] = true;

            emit PlayerRegistered(player, playerNumber);
        }

        // Check if max players reached after batch registration
        if (registeredPlayers.length >= maxPlayers) {
            registrationClosed = true;
            emit RegistrationClosed();
        }
    }

    // =============================================================
    // ================== Game Management Functions ===============
    // =============================================================

    /**
     * @notice Register a new game type
     * @param gameName Name of the game
     * @param gameAddress Address of the game contract
     */
    function registerGame(string calldata gameName, address gameAddress) external onlyOwner {
        require(gameAddress != address(0), "Invalid game address");
        require(!isGameRegistered[gameName], "Game already registered");
        require(bytes(gameName).length > 0, "Game name cannot be empty");

        gameAddresses[gameName] = gameAddress;
        isGameRegistered[gameName] = true;
        registeredGames.push(gameName);

        emit GameRegistered(gameName, gameAddress);
    }

    /**
     * @notice Initialize a new game instance
     * @param gameName Name of the game to initialize
     * @return gameId ID of the created game
     */
    function initializeGame(string calldata gameName) external onlyOwner returns (uint256) {
        require(isGameRegistered[gameName], "Game not registered");
        require(activePlayers.length > 0, "No active players");

        address gameAddress = gameAddresses[gameName];
        IGame game = IGame(gameAddress);

        // Create array of player numbers in same order as activePlayers array
        uint256[] memory playerNumbersArray = new uint256[](activePlayers.length);
        for (uint256 i = 0; i < activePlayers.length; i++) {
            address player = activePlayers[i];
            playerNumbersArray[i] = playerNumbers[player];
        }

        // Initialize new game instance
        game.initialize(activePlayers, playerNumbersArray);

        emit GameStarted(gameName, gameAddress, 0);
        return 0;
    }

    /**
     * @notice Start games for a specific game type
     * @param gameName Name of the game
     * @return success Whether all games were started successfully
     */
    function startGames(string calldata gameName) external onlyOwner returns (bool) {
        require(isGameRegistered[gameName], "Game not registered");
        IGame game = IGame(gameAddresses[gameName]);
        return game.startGames();
    }

    /**
     * @notice End expired games for a specific game type
     * @param gameName Name of the game
     */
    function endExpiredGames(string calldata gameName) external onlyOwner {
        require(isGameRegistered[gameName], "Game not registered");
        IGame game = IGame(gameAddresses[gameName]);
        game.endExpiredGames();
    }

    /**
     * @notice End all games for a specific game type
     * @param gameName Name of the game
     * @return winners Array of winners
     */
    function endGames(string calldata gameName) external onlyOwner returns (address[] memory) {
        require(isGameRegistered[gameName], "Game not registered");
        IGame game = IGame(gameAddresses[gameName]);
        return game.endGame();
    }

    /**
     * @notice Handle player elimination (called by game contracts)
     * @param player Address of the eliminated player
     */
    function playerEliminated(address player) external {
        require(isRegistered[player], "Player not registered");
        require(isActivePlayer[player], "Player already eliminated");

        // Ensure only a registered game can call this function
        bool isValidCaller = false;
        for (uint i = 0; i < registeredGames.length; i++) {
            if (msg.sender == gameAddresses[registeredGames[i]]) {
                isValidCaller = true;
                break;
            }
        }
        require(isValidCaller, "Only registered games can eliminate players");

        // Remove player from active players
        isActivePlayer[player] = false;

        // Add player to eliminated players array and set final placement
        eliminatedPlayers.push(player);
        // Calculate placement: total players - current eliminated count = placement
        // This makes first eliminated = last place, last eliminated = first place
        uint256 totalPlayers = registeredPlayers.length;
        uint256 placement = totalPlayers - eliminatedPlayers.length + 1;
        finalPlacements[player] = placement;

        // Find and remove from activePlayers array
        for (uint i = 0; i < activePlayers.length; i++) {
            if (activePlayers[i] == player) {
                // Replace with the last element and pop
                activePlayers[i] = activePlayers[activePlayers.length - 1];
                activePlayers.pop();
                break;
            }
        }

        emit PlayerEliminated(player, playerNumbers[player]);
    }

    // =============================================================
    // ===================== View Functions =======================
    // =============================================================

    /**
     * @notice Get all registered players
     * @return Array of player addresses
     */
    function getRegisteredPlayers() external view returns (address[] memory) {
        return registeredPlayers;
    }

    /**
     * @notice Get all active players
     * @return Array of active player addresses
     */
    function getActivePlayers() external view returns (address[] memory) {
        return activePlayers;
    }

    /**
     * @notice Get total number of registered players
     * @return Number of registered players
     */
    function getPlayerCount() external view returns (uint256) {
        return registeredPlayers.length;
    }

    /**
     * @notice Get total number of active players
     * @return Number of active players
     */
    function getActivePlayerCount() external view returns (uint256) {
        return activePlayers.length;
    }

    /**
     * @notice Get all registered games
     * @return Array of game names
     */
    function getRegisteredGames() external view returns (string[] memory) {
        return registeredGames;
    }

    /**
     * @notice Get player information in a game
     * @param player Address of the player
     * @return gameName Name of the game the player is in
     * @return gameId ID of the game the player is in
     * @return isActive Whether the player is still active
     * @return gameState The current state of the game (0 = Pregame, 1 = Active, 2 = Waiting, 3 = Completed)
     * @return playerNumber The player's assigned number
     */
    function getPlayerInfo(address player) external view returns (
        string memory gameName,
        uint256 gameId,
        bool isActive,
        GameState gameState,
        uint256 playerNumber
    ) {
        if (!isRegistered[player]) {
            return ("", 0, false, GameState.NotInitialized, 0);
        }

        // Get player's active status and number
        isActive = isActivePlayer[player];
        playerNumber = playerNumbers[player];

        // Store most recent match
        string memory latestGameName = "";
        uint256 latestGameId = 0;
        GameState latestGameState = GameState.NotInitialized;
        uint256 mostRecentStartTime = 0;

        // Get all registered games
        for (uint256 i = 0; i < registeredGames.length; i++) {
            string memory currentGameName = registeredGames[i];
            address gameAddress = gameAddresses[currentGameName];

            if (gameAddress != address(0)) {
                IGame game = IGame(gameAddress);
                uint256 playerGameId = game.getPlayerGameId(player);

                if (playerGameId != 0) {
                    // Get game info to check start time
                    IGame.GameInfo memory info = game.getGameInfo(playerGameId);

                    // Update latest match if this game started more recently
                    if (info.gameStartTime > mostRecentStartTime) {
                        mostRecentStartTime = info.gameStartTime;
                        latestGameName = currentGameName;
                        latestGameId = playerGameId;
                        latestGameState = info.state;
                    }
                }
            }
        }

        return (latestGameName, latestGameId, isActive, latestGameState, playerNumber);
    }

    /**
     * @notice Get player number
     * @param player Address of the player
     * @return Player number
     */
    function getPlayerNumber(address player) external view returns (uint256) {
        require(isRegistered[player], "Player not registered");
        return playerNumbers[player];
    }

    /**
     * @notice Get all active players and their player numbers
     * @return players Array of active player addresses
     * @return numbers Array of corresponding player numbers
     */
    function getActivePlayersAndNumbers() external view returns (
        address[] memory players,
        uint256[] memory numbers
    ) {
        uint256 count = activePlayers.length;
        numbers = new uint256[](count);

        // Return copy of activePlayers array and build numbers array
        players = activePlayers;
        for (uint256 i = 0; i < count; i++) {
            numbers[i] = playerNumbers[players[i]];
        }

        return (players, numbers);
    }

    /**
     * @notice Get all eliminated players in order of elimination
     * @return Array of eliminated player addresses
     */
    function getEliminatedPlayers() external view returns (address[] memory) {
        return eliminatedPlayers;
    }

    /**
     * @notice Get total number of eliminated players
     * @return Number of eliminated players
     */
    function getEliminatedPlayerCount() external view returns (uint256) {
        return eliminatedPlayers.length;
    }

    /**
     * @notice Get a player's final placement in the game (0 if still active)
     * @param player Address of the player
     * @return Final placement (totalPlayers = first eliminated, 1 = winner, 0 = still active)
     */
    function getPlayerFinalPlacement(address player) external view returns (uint256) {
        require(isRegistered[player], "Player not registered");
        return finalPlacements[player];
    }

    /**
     * @notice Get information about all game instances across all registered games
     * @return gameTypes Array of game type names
     * @return gameInstances Array of arrays containing game instances for each game type
     */
    function getGames() external view returns (
        string[] memory gameTypes,
        GameInstanceInfo[][] memory gameInstances
    ) {
        uint256 numGames = registeredGames.length;
        gameTypes = registeredGames;
        gameInstances = new GameInstanceInfo[][](numGames);

        // Get game instances for each registered game
        for (uint256 i = 0; i < numGames; i++) {
            string memory gameName = registeredGames[i];
            address gameAddress = gameAddresses[gameName];

            if (gameAddress != address(0)) {
                IGame game = IGame(gameAddress);
                gameInstances[i] = game.getGames();
            }
        }

        return (gameTypes, gameInstances);
    }

    /**
     * @notice Toggle registration status (open/closed)
     */
    function toggleRegistration() external onlyOwner {
        registrationClosed = !registrationClosed;
        if (registrationClosed) {
            emit RegistrationClosed();
        }
    }

    /**
     * @notice Set the maximum number of players allowed
     * @param _maxPlayers New maximum number of players
     */
    function setMaxPlayers(uint256 _maxPlayers) external onlyOwner {
        require(_maxPlayers > 0, "Max players must be greater than 0");
        require(_maxPlayers >= registeredPlayers.length, "Cannot set max players below current player count");
        maxPlayers = _maxPlayers;
    }

    /// @dev Register my contract on Sonic FeeM
    function registerMe() external {
        (bool _success,) = address(0xDC2B0D2Dd2b7759D97D50db4eabDC36973110830).call(
            abi.encodeWithSignature("selfRegister(uint256)", 151)
        );
        require(_success, "FeeM registration failed");
    }
}

contracts/interfaces/IGame.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

/**
 * @title IGame
 * @notice Interface for game contracts in the Ragnarok system
 */

// Game state enum
enum GameState {
    NotInitialized, // Game not yet initialized
    Pregame,    // Game not yet started
    Active,     // Game is currently active
    Waiting,    // Between rounds
    Completed   // Game has finished
}

struct GameInstanceInfo {
    uint256 gameId;
    GameState state;
    uint256 currentRound;
    uint256 activePlayerCount;

}

interface IGame {


    struct GameInfo {
        GameState state;
        uint256 currentRound;
        uint256 roundEndTime;
        uint256 gameStartTime;     // When the game was initialized
        uint256 gameEndTime;       // When the game was completed
    }

    function initialize(
        address[] calldata players,
        uint256[] calldata playerNumbers
    ) external;
    function startGames() external returns (bool);
    function endExpiredGames() external;
    function getPlayers() external view returns (address[] memory);
    function getPlayerGameId(
        address player
    ) external view returns (uint256 gameId);
    function getGameName() external view returns (string memory);
    function endGame() external returns (address[] memory winners);
    function getActivePlayers() external view returns (address[] memory);
    function getPlayerNumber(uint256 gameId, address player) external view returns (uint256);
    function getGameInfo(uint256 gameId) external view returns (GameInfo memory);
    function getGameState(uint256 gameId) external view returns (GameState);
    function getGames() external view returns (GameInstanceInfo[] memory games);
    event GameInitialized(address[] players, uint256[] playerNumbers);
}

contracts/Points.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title Points
 * @notice A contract for managing a points system with referrals
 * @dev Points are awarded based on S deposits with a 25% referral bonus
 */
contract Points is Ownable {
    // =============================================================
    // ======================= Constants ============================
    // =============================================================
    /// @notice Number of points awarded per 1 S (S)
    uint256 public constant POINTS_PER_S = 50;

    /// @notice Number of points needed to cash out 1 S
    uint256 public constant POINTS_PER_S_CASHOUT = 75;

    /// @notice Maximum total points that can be issued
    uint256 public constant MAX_POINTS = 50_000_000;

    /// @notice Minimum deposit amount (1 S = 50 points)
    uint256 public constant MIN_DEPOSIT = 1 ether;

    /// @notice Maximum number of addresses to return in pagination
    uint256 private constant MAX_PAGE_SIZE = 1000;

    /// @notice Referral bonus percentage (in basis points, e.g. 5000 = 50%)
    uint256 public referralBonusBps = 5000;

    // =============================================================
    // ===================== State Variables ========================
    // =============================================================
    /// @notice Total points issued, including referral bonuses
    uint256 public totalPointsIssued;

    /// @notice Mapping of address to their total point balance (registration + referral)
    mapping(address => uint256) public points;

    /// @notice Mapping of address to their withdrawable referral point balance
    mapping(address => uint256) public referralPoints;

    /// @notice List of all addresses that have received points
    address[] public addressList;

    /// @notice Tracks if an address has received points
    mapping(address => bool) public hasPoints;

    /// @notice Mapping of authorized contracts that can award points directly
    mapping(address => bool) public authorizedContracts;

    // =============================================================
    // ==================== Referral System ========================
    // =============================================================
    /// @notice Maps referral codes to their owners
    mapping(string => address) public referralCodeToAddress;

    /// @notice Maps addresses to their referral codes
    mapping(address => string) public addressToReferralCode;

    /// @notice Maps addresses to the referral code they used
    mapping(address => string) public usedReferralCode;

    // =============================================================
    // ======================== Events =============================
    // =============================================================
    /// @notice Emitted when points are awarded
    /// @param recipient Address receiving points
    /// @param amount Number of points awarded
    event PointsAwarded(address indexed recipient, uint256 amount);

    /// @notice Emitted when S is withdrawn
    /// @param owner Address receiving the withdrawal
    /// @param amount Amount of S withdrawn
    event Withdrawn(address indexed owner, uint256 amount);

    /// @notice Emitted when a referral code is registered
    /// @param user Address registering the code
    /// @param code The referral code registered
    event ReferralCodeRegistered(address indexed user, string code);

    /// @notice Emitted when a referral code is used
    /// @param user Address using the code
    /// @param code Referral code used
    /// @param referrer Address that owns the referral code
    event ReferralUsed(address indexed user, string code, address indexed referrer);

    /// @notice Emitted when referral points are cashed out
    /// @param user Address cashing out points
    /// @param points Number of points cashed out
    /// @param ethAmount Amount of ETH received
    event ReferralPointsCashedOut(address indexed user, uint256 points, uint256 ethAmount);

    /// @notice Emitted when ETH is deposited for cashouts without points being awarded
    /// @param depositor Address that deposited the ETH
    /// @param amount Amount of ETH deposited
    event CashoutDepositReceived(address indexed depositor, uint256 amount);

    /// @notice Emitted when a contract is authorized or deauthorized
    /// @param contractAddress Address of the contract
    /// @param authorized Whether the contract is authorized
    event ContractAuthorizationChanged(address indexed contractAddress, bool authorized);

    /// @notice Emitted when points are awarded by an authorized contract
    /// @param recipient Address receiving points
    /// @param amount Number of points awarded
    /// @param authorizedContract Contract that awarded the points
    event PointsAwardedByContract(address indexed recipient, uint256 amount, address indexed authorizedContract);

    // =============================================================
    // ======================== Errors =============================
    // =============================================================
    error PointsCapReached(uint256 remaining);
    error InvalidDeposit(uint256 min);
    error InvalidReferralCode();
    error ReferralCodeTaken();
    error ReferralCodeTooLong();

    // =============================================================
    // ======================== Modifiers ===========================
    // =============================================================

    modifier onlyAuthorizedContract() {
        require(authorizedContracts[msg.sender], "Not authorized contract");
        _;
    }

    constructor() Ownable(msg.sender) {}

    // =============================================================
    // ==================== Deposit Functions ======================
    // =============================================================

    /// @notice Deposit S to receive points
    function deposit() external payable {
        _awardPoints(msg.sender);
    }

    /// @notice Deposit ETH to be used for cashouts without receiving points
    function depositForCashouts() external payable {
        require(msg.value > 0, "Must deposit some ETH");
        emit CashoutDepositReceived(msg.sender, msg.value);
    }

    /// @notice Deposit S to award points to another address
    /// @param recipient Address to receive the points
    function depositFor(address recipient) external payable {
        require(recipient != address(0), "Invalid recipient");
        _awardPoints(recipient);
    }

    /// @notice Deposit S with a referral code to award points to another address
    /// @param recipient Address to receive the points
    /// @param referralCode Referral code to use for the deposit
    function depositFor(address recipient, string calldata referralCode) external payable {
        require(recipient != address(0), "Invalid recipient");
        require(referralCodeToAddress[referralCode] != address(0), "Invalid referral code");

        // Only set referral code if user hasn't used one before
        if (bytes(usedReferralCode[recipient]).length == 0) {
            usedReferralCode[recipient] = referralCode;
            emit ReferralUsed(recipient, referralCode, referralCodeToAddress[referralCode]);
        }

        _awardPoints(recipient);
    }

    // =============================================================
    // ==================== Referral Functions =====================
    // =============================================================

    /// @notice Register or update a referral code
    /// @param code The desired referral code
    function registerReferralCode(string calldata code) external {
        // Validate code length
        if (bytes(code).length > 20) {
            revert ReferralCodeTooLong();
        }

        // Validate code is not empty
        if (bytes(code).length == 0) {
            revert InvalidReferralCode();
        }

        // Check if code is already taken by someone else
        address currentOwner = referralCodeToAddress[code];
        if (currentOwner != address(0) && currentOwner != msg.sender) {
            revert ReferralCodeTaken();
        }

        // Check if user already has a referral code
        if (bytes(addressToReferralCode[msg.sender]).length > 0) {
            revert ReferralCodeTaken();
        }

        // Register the new code
        referralCodeToAddress[code] = msg.sender;
        addressToReferralCode[msg.sender] = code;

        emit ReferralCodeRegistered(msg.sender, code);
    }

    // =============================================================
    // ==================== Internal Functions =====================
    // =============================================================

    /// @notice Award points based on S value and handle referral bonuses
    /// @param recipient Address to receive the points
    function _awardPoints(address recipient) internal {
        if (msg.value < MIN_DEPOSIT) {
            revert InvalidDeposit(MIN_DEPOSIT);
        }

        // Calculate base points from S value
        uint256 pointsToAward = (msg.value / 1e18) * POINTS_PER_S;
        pointsToAward += (msg.value % 1e18) * POINTS_PER_S / 1e18;

        // Track total points needed including potential referral bonus
        uint256 totalPointsNeeded = pointsToAward;

        // Handle referral bonus if applicable
        string memory referralCode = usedReferralCode[recipient];
        if (bytes(referralCode).length > 0) {
            // Calculate and award referral bonus using configurable percentage
            uint256 referralBonus = (pointsToAward * referralBonusBps) / 10000;
            totalPointsNeeded += referralBonus;

            // Verify points cap with bonus included
            if (totalPointsIssued + totalPointsNeeded > MAX_POINTS) {
                revert PointsCapReached(getRemainingPoints());
            }

            // Award bonus to referrer
            address referrer = referralCodeToAddress[referralCode];
            if (referrer != address(0)) {
                points[referrer] += referralBonus;
                referralPoints[referrer] += referralBonus;
                emit PointsAwarded(referrer, referralBonus);
            }
        } else {
            // Verify points cap for non-referral deposit
            if (totalPointsIssued + pointsToAward > MAX_POINTS) {
                revert PointsCapReached(getRemainingPoints());
            }
        }

        // Award points to recipient
        points[recipient] += pointsToAward;

        // Add recipient to address list if first time
        if (!hasPoints[recipient]) {
            addressList.push(recipient);
            hasPoints[recipient] = true;
        }

        totalPointsIssued += totalPointsNeeded;
        emit PointsAwarded(recipient, pointsToAward);
    }

    // =============================================================
    // ==================== View Functions =========================
    // =============================================================

    /// @notice Get points balance for an account
    /// @param account Address to check
    /// @return total Total points owned by the account
    /// @return withdrawable Number of points that can be withdrawn
    function getPoints(address account) external view returns (uint256 total, uint256 withdrawable) {
        return (points[account], referralPoints[account]);
    }

    /// @notice Get remaining points that can be issued
    /// @return Number of points remaining before cap
    function getRemainingPoints() public view returns (uint256) {
        return totalPointsIssued >= MAX_POINTS ? 0 : MAX_POINTS - totalPointsIssued;
    }

    /// @notice Get total number of addresses with points
    /// @return Number of addresses in the system
    function getAddressCount() external view returns (uint256) {
        return addressList.length;
    }

    /// @notice Get paginated list of addresses and their balances
    /// @param start Starting index
    /// @param size Number of addresses to return
    /// @return addresses Array of addresses
    /// @return balances Array of point balances
    /// @return withdrawableBalances Array of withdrawable point balances
    function getAddressesPaginated(uint256 start, uint256 size) 
        external 
        view 
        returns (
            address[] memory addresses, 
            uint256[] memory balances,
            uint256[] memory withdrawableBalances
        ) 
    {
        require(start < addressList.length, "Invalid start");
        require(size > 0 && size <= MAX_PAGE_SIZE, "Invalid size");

        uint256 end = Math.min(start + size, addressList.length);
        uint256 length = end - start;

        addresses = new address[](length);
        balances = new uint256[](length);
        withdrawableBalances = new uint256[](length);

        for (uint256 i = 0; i < length; i++) {
            addresses[i] = addressList[start + i];
            balances[i] = points[addresses[i]];
            withdrawableBalances[i] = referralPoints[addresses[i]];
        }
    }

    /// @notice Get top 100 addresses by point balance
    /// @return topAddresses Array of addresses
    /// @return topBalances Array of point balances
    /// @return withdrawableBalances Array of withdrawable point balances
    function getLeaderboard() public view returns (
        address[] memory topAddresses, 
        uint256[] memory topBalances,
        uint256[] memory withdrawableBalances
    ) {
        uint256 length = Math.min(addressList.length, 100);
        topAddresses = new address[](length);
        topBalances = new uint256[](length);
        withdrawableBalances = new uint256[](length);

        // Copy addresses and balances
        for (uint256 i = 0; i < length; i++) {
            topAddresses[i] = addressList[i];
            topBalances[i] = points[addressList[i]];
            withdrawableBalances[i] = referralPoints[addressList[i]];
        }

        // Sort using QuickSort
        if (length > 1) {
            quickSort(topAddresses, topBalances, withdrawableBalances, 0, int256(length - 1));
        }
    }

    /// @notice QuickSort implementation for sorting addresses by balance
    function quickSort(
        address[] memory addresses,
        uint256[] memory balances,
        uint256[] memory withdrawable,
        int256 left,
        int256 right
    ) internal pure {
        if (left >= right) return;

        uint256 pivot = balances[uint256(left + (right - left) / 2)];

        int256 i = left;
        int256 j = right;
        while (i <= j) {
            while (balances[uint256(i)] > pivot) i++;
            while (balances[uint256(j)] < pivot) j--;

            if (i <= j) {
                (addresses[uint256(i)], addresses[uint256(j)]) = (addresses[uint256(j)], addresses[uint256(i)]);
                (balances[uint256(i)], balances[uint256(j)]) = (balances[uint256(j)], balances[uint256(i)]);
                (withdrawable[uint256(i)], withdrawable[uint256(j)]) = (withdrawable[uint256(j)], withdrawable[uint256(i)]);
                i++;
                j--;
            }
        }

        if (left < j) quickSort(addresses, balances, withdrawable, left, j);
        if (i < right) quickSort(addresses, balances, withdrawable, i, right);
    }

    // =============================================================
    // ==================== Admin Functions ========================
    // =============================================================

    /// @notice Withdraw accumulated S (owner only)
    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "Nothing to withdraw");

        (bool success, ) = msg.sender.call{value: balance}("");
        require(success, "Withdrawal failed");

        emit Withdrawn(msg.sender, balance);
    }

    /// @notice Register contract with Sonic FeeM
    function registerMe() external {
        (bool _success,) = address(0xDC2B0D2Dd2b7759D97D50db4eabDC36973110830).call(
            abi.encodeWithSignature("selfRegister(uint256)", 151)
        );
        require(_success, "FeeM registration failed");
    }

    /// @notice Update the referral bonus percentage
    /// @param newBonusBps New bonus percentage in basis points (e.g. 2500 = 25%)
    function setReferralBonus(uint256 newBonusBps) external onlyOwner {
        require(newBonusBps <= 10000, "Bonus cannot exceed 100%");
        referralBonusBps = newBonusBps;
    }

    /// @notice Manually assign points to an address (owner only)
    /// @param recipient Address to receive points
    /// @param amount Number of points to assign
    /// @param isReferralPoints Whether these points should be withdrawable as referral points
    function assignPoints(address recipient, uint256 amount, bool isReferralPoints) external onlyOwner {
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");
        require(totalPointsIssued + amount <= MAX_POINTS, "Would exceed points cap");

        // Update points balances
        points[recipient] += amount;
        if (isReferralPoints) {
            referralPoints[recipient] += amount;
        }

        // Add recipient to address list if first time
        if (!hasPoints[recipient]) {
            addressList.push(recipient);
            hasPoints[recipient] = true;
        }

        totalPointsIssued += amount;
        emit PointsAwarded(recipient, amount);
    }

    /// @notice Authorize or deauthorize a contract to award points directly
    /// @param contractAddress Address of the contract to authorize/deauthorize
    /// @param authorized Whether to authorize or deauthorize the contract
    function setContractAuthorization(address contractAddress, bool authorized) external onlyOwner {
        require(contractAddress != address(0), "Invalid contract address");

        authorizedContracts[contractAddress] = authorized;
        emit ContractAuthorizationChanged(contractAddress, authorized);
    }

    /// @notice Migrate a referral code for a user during contract migration (owner only)
    /// @param user Address of the user to assign the referral code to
    /// @param code The referral code to assign
    function migrateReferralCode(address user, string calldata code) external onlyOwner {
        require(user != address(0), "Invalid user address");

        // Validate code length
        if (bytes(code).length > 20) {
            revert ReferralCodeTooLong();
        }

        // Validate code is not empty
        if (bytes(code).length == 0) {
            revert InvalidReferralCode();
        }

        // Check if code is already taken by someone else
        address currentOwner = referralCodeToAddress[code];
        if (currentOwner != address(0) && currentOwner != user) {
            revert ReferralCodeTaken();
        }

        // Clear any existing referral code for this user
        string memory existingCode = addressToReferralCode[user];
        if (bytes(existingCode).length > 0) {
            delete referralCodeToAddress[existingCode];
        }

        // Register the new code
        referralCodeToAddress[code] = user;
        addressToReferralCode[user] = code;

        emit ReferralCodeRegistered(user, code);
    }

    /// @notice Migrate used referral code for a user during contract migration (owner only)
    /// @param user Address of the user who used the referral code
    /// @param referralCode The referral code they used
    function migrateUsedReferralCode(address user, string calldata referralCode) external onlyOwner {
        require(user != address(0), "Invalid user address");
        require(bytes(referralCode).length > 0, "Invalid referral code");

        // Verify the referral code exists and is owned by someone
        address referrer = referralCodeToAddress[referralCode];
        require(referrer != address(0), "Referral code does not exist");

        usedReferralCode[user] = referralCode;
        emit ReferralUsed(user, referralCode, referrer);
    }

    /// @notice Cash out referral points for ETH
    /// @param amount Number of referral points to cash out
    function cashOutReferralPoints(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");
        require(referralPoints[msg.sender] >= amount, "Insufficient referral points");

        // Calculate ETH amount rounded down to 2 decimal places
        // First calculate the result in cents (multiply by 100)
        uint256 centsAmount = (amount * 100) / POINTS_PER_S_CASHOUT;
        // Convert cents back to ETH with 18 decimals (multiply by 1e16 since we're in cents)
        uint256 ethAmount = centsAmount * 1e16;
        require(address(this).balance >= ethAmount, "Insufficient contract balance");

        // Update points before transfer
        referralPoints[msg.sender] -= amount;
        points[msg.sender] -= amount;
        totalPointsIssued -= amount;

        // Transfer ETH
        (bool success, ) = msg.sender.call{value: ethAmount}("");
        require(success, "Transfer failed");

        emit ReferralPointsCashedOut(msg.sender, amount, ethAmount);
    }

    // =============================================================
    // ================ Authorized Contract Functions ===============
    // =============================================================

    /// @notice Award points directly to a recipient (only callable by authorized contracts)
    /// @param recipient Address to receive the points
    /// @param pointsAmount Number of points to award
    /// @param isWithdrawable Whether these points should be withdrawable as referral points
    function awardPointsForPayout(address recipient, uint256 pointsAmount, bool isWithdrawable) external onlyAuthorizedContract {
        require(recipient != address(0), "Invalid recipient");
        require(pointsAmount > 0, "Points amount must be greater than 0");

        // Verify points cap
        if (totalPointsIssued + pointsAmount > MAX_POINTS) {
            revert PointsCapReached(getRemainingPoints());
        }

        // Award points to recipient
        points[recipient] += pointsAmount;

        // If withdrawable, also add to referral points
        if (isWithdrawable) {
            referralPoints[recipient] += pointsAmount;
        }

        // Add recipient to address list if first time
        if (!hasPoints[recipient]) {
            addressList.push(recipient);
            hasPoints[recipient] = true;
        }

        totalPointsIssued += pointsAmount;
        emit PointsAwardedByContract(recipient, pointsAmount, msg.sender);
    }
}

